const e={word:"assign",bep:"əˈsaɪn",aep:"əˈsaɪn",form:{yx:"assign",sd:"assigns",xf:"assigning",gf:"assigned",gq:"assigned"}},s=[{label:"v",items:[{mean:"分配（某物）；分派，布置（工作、任务等）",dict:"to give sb sth that they can use, or some work or responsibility",comb:["~ sth (to sb)"," ~ (sb) sth"],demo:[{en:"The two large classrooms have been assigned to us.",zh:"这两间大教室‘分配’给了我们。"},{en:"The teacher assigned a different task to each of the children.",zh:"老师给每个儿童‘布置’的作业都不相同。"},{en:"We have been assigned the two large classrooms.",zh:"我们‘分得’了这两间大教室。"},{en:"The teacher assigned each of the children a different task.",zh:"老师给每个儿童都‘布置’了不同的作业。"},{zhan_img:"http://assets.baicizhan.com/r/i_1_4595_0_2_20150808122912.jpg",zhan_logo:"http://assets.baicizhan.com/r/d_1_4595_0_2_20150808122912.png",en:"Our teacher assigns us some homework.",zh:"我们的老师给我们‘布置了’一些家庭作业。"}],zhan_tv:"http://assets.baicizhan.com/word_tv/assign.mp4"},{mean:"指定；指派",dict:"to provide a person for a particular task or position",comb:["~ sb (to sth/as sth)"],demo:[{en:"They&apos;ve assigned their best man to the job.",zh:"他们指派了最优秀的人担任这项工作。"},{en:"British forces have been assigned to help with peacekeeping.",zh:"英国军队被派遣协助维持和平。"}]},{mean:"委派；派遣",dict:"to send a person to work under the authority of sb or in a particular group",comb:["VN~ sb to sb/sth"],demo:[{en:"I was assigned to B platoon.",zh:"我被派到B排工作。"}]},{mean:"确定（价值、功能、时间、地点）",dict:"to say that sth has a particular value or function, or happens at a particular time or place",comb:["~ sth to sth"],demo:[{en:"Assign a different colour to each different type of information.",zh:"给每类信息分别确定一种颜色。"},{en:"The painting cannot be assigned an exact date.",zh:"这幅画的年代确定不了。"}]},{mean:"转让，让与（财产、权利）",dict:"to say that your property or rights now belong to sb else",comb:["VN~ sth to sb"],demo:[{en:"The agreement assigns copyright to the publisher.",zh:"协议规定将版权转让给出版商。"}]}]}];export{e as basic,s as detail};
