const e={word:"guarantee",bep:"ˌɡærənˈtiː",aep:"ˌɡærənˈtiː",form:{yx:"guarantee",fs:"guarantees",sd:"guarantees",xf:"guaranteeing",gf:"guaranteed",gq:"guaranteed"}},a=[{label:"n",items:[{mean:"保证；担保",dict:"a firm promise that you will do sth or that sth will happen   同义词： assurance",demo:[{en:"to give a guarantee of good behaviour",zh:"保证行为端正"},{en:"He gave me a guarantee that it would never happen again.",zh:"他向我保证这种事情绝不会再发生。"},{en:"They are demanding certain guarantees before they sign the treaty.",zh:"他们要求得到某些保证后才签署条约。"}],zhan_tv:"http://assets.baicizhan.com/word_tv/music_guarantee.mp4"},{mean:"保修单；保用证书",dict:"a written promise given by a company that sth you buy will be replaced or repaired without payment if it goes wrong within a particular period   同义词： warranty",demo:[{en:"We provide a 5-year guarantee against rust.",zh:"我们保证5年不生锈。"},{en:"The watch is still under guarantee .",zh:"这只手表仍在保修期内。"},{en:"The television comes with a year&apos;s guarantee.",zh:"这电视机有一年的保修期。"},{en:"a money-back guarantee",zh:"退货保证"}]},{mean:"起保证作用的事物",dict:"something that makes sth else certain to happen",comb:["~ (of sth) | ~ (that…)"],demo:[{en:"Career success is no guarantee of happiness.",zh:"事业成功并不是幸福的保证。"},{en:"There&apos;s no guarantee that she&apos;ll come (= she may not come) .",zh:"不能保证她一定会来。"}]},{mean:"保证金；抵押品",dict:"money or sth valuable that you give or promise to a bank, for example, to make sure that you will do what you have promised",demo:[{en:"We had to offer our house as a guarantee when getting the loan.",zh:"我们在贷款时不得不拿房子作抵押。"}]}]},{label:"v",items:[{mean:"保证；担保；保障",dict:"to promise to do sth; to promise sth will happen",demo:[{en:"Basic human rights, including freedom of speech, are now guaranteed.",zh:"现在，包括言论自由在内的基本人权已有了‘保障’。"},{en:"We cannot guarantee (that) our flights will never be delayed.",zh:"我们不能‘保证’我们的所有航班均不误点。"},{en:"The ticket will guarantee you free entry.",zh:"这张票可‘保证’你免费入场。"},{en:"We guarantee to deliver your goods within a week.",zh:"我们‘保证’一周内交货。"},{zhan_img:"http://assets.baicizhan.com/r/i_16_4761_0_8_160504161526.jpg",zhan_logo:"http://assets.baicizhan.com/r/d_16_4761_0_4_20150808224409.png",en:"Using Baicizhan guarantees that you will pass your English exams.",zh:"百词斩‘包’你秒杀所有英语考试！"}]},{mean:"提供（产品）保修单（免费掉换或修理有问题的产品）",dict:"to give a written promise to replace or repair a product free if it goes wrong",comb:["VN~ sth (against sth)"],demo:[{en:"This iron is guaranteed for a year against faulty workmanship.",zh:"这种熨斗如有工艺缺陷可保修一年。"}]},{mean:"使必然发生；确保",dict:"to make sth certain to happen",demo:[{en:"Tonight&apos;s victory guarantees the team&apos;s place in the final.",zh:"今晚的胜利确保这个队能进入决赛。"},{en:"These days getting a degree doesn&apos;t guarantee you a job.",zh:"如今获得学位并不能保证你就有工作。"}]},{mean:"肯定…必然发生",dict:"to be certain that sth will happen",demo:[{en:"You can guarantee (that) the children will start being naughty as soon as they have to go to bed.",zh:"孩子一到该上床睡觉时一定就不听话了。"}]},{mean:"承诺对…负法律责任；为…作保",dict:"to agree to be legally responsible for sth or for doing sth",demo:[{en:"to guarantee a bank loan",zh:"为银行贷款作保"},{en:"to guarantee to pay sb&apos;s debts",zh:"为某人的债务作保"},{en:"I guarantee that he will appear in court.",zh:"我保证他会出庭。"}]}]}];export{e as basic,a as detail};
